            <a href='/word_detail.php?id=1374' onmouseover="showToolTip(this, '聯合 liánhé', 'to combine / to join / to unite')" onmouseout="hideToolTip()">联合</a><a href='/word_detail.php?id=262' onmouseover="showToolTip(this, '關係 guānxì', 'relations')" onmouseout="hideToolTip()">关系</a>，<a href='/word_detail.php?id=9268' onmouseover="showToolTip(this, 'fēnjù', 'clause')" onmouseout="hideToolTip()">分句</a><a href='/word_detail.php?id=1000' onmouseover="showToolTip(this, '分別 fēnbié', 'to differentiate / to distinguish')" onmouseout="hideToolTip()">分别</a><a href='/word_detail.php?id=1514' onmouseover="showToolTip(this, '說明 shuōmíng', 'to explain')" onmouseout="hideToolTip()">说明</a><a href='/word_detail.php?id=312' onmouseover="showToolTip(this, '兩 liǎng', 'two')" onmouseout="hideToolTip()">两</a><a href='/word_detail.php?id=2274' onmouseover="showToolTip(this, 'jiàn', 'a document')" onmouseout="hideToolTip()">件</a><a href='/word_detail.php?id=9642' onmouseover="showToolTip(this, 'shìqíng', 'affair / matter / thing')" 
